<template><div class="module-view module-layout"><div class="module-view-header"><div class="module-view-icon"><font-awesome-icon icon="credit-card" /></div><div class="module-view-info"><h1>Paga Licencias</h1></div></div>
  <div class="module-view-content"><div class="municipal-card"><div class="municipal-card-body"><div class="form-row"><div class="form-group"><label class="municipal-form-label">Licencia</label><input class="municipal-form-control" v-model="filters.licencia"/></div></div><div class="button-group"><button class="btn-municipal-primary" :disabled="loading" @click="pagar">Pagar</button></div></div></div></div></template>
<script setup>import { ref } from 'vue'; import { useApi } from '@/composables/useApi'; const { loading, execute } = useApi(); const BASE_DB='INFORMIX'; const OP='RECAUDADORA_PAGALICFRM'; const filters=ref({ licencia:'' }); async function pagar(){ try{ await execute(OP, BASE_DB, [ { name:'licencia', type:'C', value:String(filters.value.licencia||'') } ]) }catch(e){} }</script>
