<?php $pdo = new PDO("pgsql:host=192.168.6.146;port=5432;dbname=mercados", "refact", "FF)-BQk2", [PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION]); echo "Valores de vigencia:\n"; $vigencia = $pdo->query("SELECT DISTINCT vigencia, COUNT(*) as total FROM publico.ta_11_locales GROUP BY vigencia ORDER BY vigencia")->fetchAll(); foreach ($vigencia as $v) { echo "  " . ($v["vigencia"] ?? "NULL") . ": " . $v["total"] . " registros\n"; } echo "\nValores de bloqueo:\n"; $bloqueo = $pdo->query("SELECT DISTINCT bloqueo, COUNT(*) as total FROM publico.ta_11_locales GROUP BY bloqueo ORDER BY bloqueo")->fetchAll(); foreach ($bloqueo as $b) { echo "  " . ($b["bloqueo"] ?? "NULL") . ": " . $b["total"] . " registros\n"; } ?>
